package if_else;

public class Exampl6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=13;
		if(num%2==0) {
			System.out.println("Number is Even");
			
		}
		else if(num%2!=0) {
			System.out.println("Number is Odd");
		}
		else {
			System.out.println("Number is Zero");
		}
	}

}
